args:
/* Fixtures Table */
.controls-bar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 20px;
    padding: 10px;
    background-color: #f1f5f9;
    border-radius: 8px;
    border: 1px solid var(--border-color);
}

.selection-controls {
    display: flex;
    gap: 10px;
}

.control-btn {
    padding: 8px 16px;
    border: 1px solid var(--border-color);
    border-radius: 6px;
    background: white;
    cursor: pointer;
    font-size: 0.9rem;
    transition: all 0.2s;
}

.control-btn:hover {
    background: #f8fafc;
    border-color: var(--secondary-color);
}

.btn-primary-lg {
    background: var(--primary-color);
    color: white;
    border: none;
    font-weight: 600;
    padding: 10px 20px;
    font-size: 1rem;
    box-shadow: var(--shadow);
}

.btn-primary-lg:hover {
    background: var(--secondary-color);
    transform: translateY(-2px);
    box-shadow: var(--shadow-lg);
}

.btn-primary-lg:disabled {
    background: #ccc;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
}

.fixtures-table {
    width: 100%;
    border-collapse: collapse;
    background: white;
    border-radius: 8px;
    overflow: hidden;
    box-shadow: var(--shadow);
}

.fixtures-table th,
.fixtures-table td {
    padding: 12px 16px;
    text-align: left;
    border-bottom: 1px solid var(--border-color);
}

.fixtures-table th {
    background-color: #f8fafc;
    font-weight: 600;
    color: var(--text-color);
}

.fixtures-table tr:hover {
    background-color: #f1f5f9;
}

.fixtures-table .checkbox-col {
    width: 40px;
    text-align: center;
}

.fixtures-table .time-col {
    font-family: monospace;
    font-weight: 600;
}

.fixtures-table .team-col {
    display: flex;
    align-items: center;
    gap: 8px;
}

.fixtures-table .team-col img {
    width: 24px;
    height: 24px;
    object-fit: contain;
}

.fixtures-table .vs-col, .fixtures-table .score-col {
    text-align: center;
    font-weight: bold;
    color: #9ca3af;
}

.fixtures-table input[type="checkbox"] {
    width: 18px;
    height: 18px;
    cursor: pointer;
}
