args:
/* Fixtures Table */
.controls-bar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 20px;
    padding: 10px;
    background-color: #f1f5f9;
    border-radius: 8px;
    border: 1px solid var(--border-color);
}

.selection-controls {
    display: flex;
    gap: 10px;
}

.control-btn {
    padding: 8px 16px;
    border: 1px solid var(--border-color);
    border-radius: 6px;
    background: white;
    cursor: pointer;
    font-size: 0.9rem;
    transition: all 0.2s;
}

.control-btn:hover {
    background: #f8fafc;
    border-color: var(--secondary-color);
}

.btn-primary-lg {
    background: var(--primary-color);
    color: white;
    border: none;
    font-weight: 600;
    padding: 10px 20px;
    font-size: 1rem;
    box-shadow: var(--shadow);
}

.btn-primary-lg:hover {
    background: var(--secondary-color);
    transform: translateY(-2px);
    box-shadow: var(--shadow-lg);
}

.btn-primary-lg:disabled {
    background: #ccc;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
}

.fixtures-table {
    width: 100%;
    border-collapse: collapse;
    background: white;
    border-radius: 8px;
    overflow: hidden;
    box-shadow: var(--shadow);
}

.fixtures-table th,
.fixtures-table td {
    padding: 12px 16px;
    text-align: left;
    border-bottom: 1px solid var(--border-color);
}

.fixtures-table th {
    background-color: #f8fafc;
    font-weight: 600;
    color: var(--text-color);
}

.fixtures-table tr:hover {
    background-color: #f1f5f9;
}

.fixtures-table .checkbox-col {
    width: 40px;
    text-align: center;
}

.fixtures-table .time-col {
    font-family: monospace;
    font-weight: 600;
}

.fixtures-table .team-col {
    display: flex;
    align-items: center;
    gap: 8px;
}

.fixtures-table .team-col img {
    width: 24px;
    height: 24px;
    object-fit: contain;
}

.fixtures-table .vs-col, .fixtures-table .score-col {
    text-align: center;
    font-weight: bold;
    color: #9ca3af;
}

.fixtures-table input[type="checkbox"] {
    width: 18px;
    height: 18px;
    cursor: pointer;
}

/* Favorites Bar */
.quick-select {
    /* Hidden by default, JS controls visibility */
    background: white;
    padding: 15px 20px;
    margin: 0px 0 20px 0; /* Tight to header */
    border-radius: 12px;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.05);
    text-align: center;
    border: 1px solid #f1f5f9;
}

.quick-select h3 {
    margin: 0 0 12px 0;
    font-size: 0.9rem;
    color: #64748b;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.team-buttons {
    display: flex;
    justify-content: center;
    gap: 12px;
    flex-wrap: wrap;
}

.fav-chip {
    display: flex;
    align-items: center;
    gap: 10px;
    background: #f8fafc;
    border: 1px solid #e2e8f0;
    padding: 8px 16px;
    border-radius: 30px;
    cursor: pointer;
    transition: all 0.2s ease;
    user-select: none;
}

.fav-chip:hover {
    background: #eff6ff;
    border-color: #60a5fa;
    transform: translateY(-2px);
    box-shadow: 0 4px 6px -2px rgba(59, 130, 246, 0.1);
}

.fav-chip img {
    width: 24px;
    height: 24px;
    object-fit: contain;
}

.fav-chip span {
    font-weight: 600;
    color: #334155;
    font-size: 0.95rem;
}

.fav-chip.empty-msg {
    cursor: default;
    background: transparent;
    border: none;
    color: #94a3b8;
}
